import logo from './logo.svg';
import './App.css';



let state;
state = {
    count: 0,
    tags:[
        "tag1","tag2","tag3","tag4",
    ]
};

function formateCount(){
    let count = state.count
    return count === 0 ? "zero" : count;
}

function countColour() {
    let classname="btn  m-2 btn-sm btn-"
    classname+=state.count ===0 ? "warning" : "primary"
    return classname
}

function increment() {
    state.count+=1;
}
function decrement() {
    state.count-=1;

}

function App() {
    return (
    <div >
      <header >

          <link href="https://cdn.jsdelivr.net/npm/bootstrap@5.0.2/dist/css/bootstrap.min.css" rel="stylesheet"
                integrity="sha384-EVSTQN3/azprG1Anm3QDgpJLIm9Nao0Yz1ztcQTwFspd3yD65VohhpuuCOmLASjC"
                crossOrigin="anonymous"></link>

          <script src="https://cdn.jsdelivr.net/npm/bootstrap@5.0.2/dist/js/bootstrap.bundle.min.js"
                  integrity="sha384-MrcW6ZMFYlzcLA8Nl+NtUVF0sA7MsXsP1UyJoMp4YLEuNSfAP+JcXn/tWtIaxVXM"
                  crossOrigin="anonymous"></script>
      </header>
        <span className={countColour()}>{formateCount()}</span>
        <button onClick={increment()} className="btn btn-secondary btn-sm m-2">increment</button>
        <button onClick={decrement()} className="btn btn-secondary btn-sm">decrement</button>
        <ul>
            {state.tags.map(tag => <li key={tag}>{tag}</li>)}
        </ul>

    </div>
  );
}
export default App;
